String[] texts = ["hello world", "worldd hello"]
String[] expectedResult = ["(?s).*world.*", "(?s).*world.*"]
Integer arraySize = new Integer( texts.size())
testRunner.testCase.setPropertyValue("arraySize", arraySize.toString())
Integer counter = new Integer(testRunner.testCase.getPropertyValue("counter"))
if(counter >= arraySize) 
{
	testRunner.gotoStepByName("NextGroovy")
} else
{
testRunner.testCase.setPropertyValue("text", texts[counter]) 
testRunner.testCase.setPropertyValue("expectedResult", expectedResult[counter]) 
}

Integer arraySize = new Integer( testRunner.testCase.getPropertyValue("arraySize"))
Integer counter = new Integer(testRunner.testCase.getPropertyValue("counter"))
if (counter < arraySize)
{
	counter += 1
	testRunner.testCase.setPropertyValue("counter", counter.toString())
	testRunner.gotoStepByName("SetUpGroovy")
}
